-- Run on real generation_plant table
WITH t AS
(SELECT a.generation_plant_id, a.name, a.gen_tech, a.load_zone_id, sum(b.capacity) as capacity_limit_mw
FROM generation_plant a
JOIN generation_plant_existing_and_planned b
ON a.generation_plant_id = b.generation_plant_id
WHERE generation_plant_existing_and_planned_scenario_id = 21
AND a.load_zone_id IN (8, 9, 28)
group by a.generation_plant_id)

UPDATE switch.generation_plant a
SET capacity_limit_mw = t.capacity_limit_mw
FROM t
WHERE a.generation_plant_id = t.generation_plant_id;

