-- Run on real generation_plant_cost table
WITH t AS
(SELECT a.generation_plant_id, a.name, a.load_zone_id
FROM generation_plant a
JOIN generation_plant_existing_and_planned b
ON a.generation_plant_id = b.generation_plant_id
JOIN generation_plant_cost c
ON b.generation_plant_id = c.generation_plant_id
WHERE generation_plant_existing_and_planned_scenario_id = 21
AND generation_plant_cost_scenario_id = 23
AND c.overnight_cost != 0)

UPDATE switch.generation_plant_cost a
SET overnight_cost = 0, fixed_o_m = 0
FROM t
WHERE a.generation_plant_id = t.generation_plant_id
AND a.generation_plant_cost_scenario_id = 23;